\[\begin{equation*}
        \begin{aligned}
            \max_{\{x_1, \ldots, x_n\}}\  & f(x_1, \ldots, x_n;\ p_1, \ldots, p_q)          \\
            \text{subject } \quad       & h_i(x_1, \ldots, x_n;\ p_1, \ldots, p_q) = 0, \quad \forall = 1, \ldots, k; \\
                                          & g_j(x_1, \ldots, x_n;\ p_1, \ldots, p_q) \ge 0, \quad \forall j = 1, \ldots, m.
        \end{aligned}
\end{equation*}\]Lagrangian function:\[\begin{equation*}
        \begin{aligned}
             & \mathcal{L}(x_1, \ldots, x_n;\ p_1, \ldots, p_q;\ \lambda_1, \ldots, \lambda_k;\ \mu_1, \ldots, \mu_m) = \\
             & f(x_1,\ldots, p_q) + \sum_{=1}^{k}\lambda_i h_i (x_1, \ldots, p_q) + \sum_{j=1}^{m}\mu_j g_j (x_1, \ldots, p_q)
        \end{aligned}
\end{equation*}\]

\[\begin{equation*}
        \begin{aligned}
            \min_{\{x_1, \ldots, x_n\}}\  & f(x_1, \ldots, x_n;\ p_1, \ldots, p_q)          \\
            \text{subject } \quad       & h_i(x_1, \ldots, x_n;\ p_1, \ldots, p_q) = 0, \quad \forall = 1, \ldots, k; \\
                                          & g_j(x_1, \ldots, x_n;\ p_1, \ldots, p_q) \ge 0, \quad \forall j = 1, \ldots, m.
        \end{aligned}
\end{equation*}\]Lagrangian function:\[\begin{equation*}
        \begin{aligned}
             & \mathcal{L}(x_1, \ldots, x_n;\ p_1, \ldots, p_q;\ \lambda_1, \ldots, \lambda_k;\ \mu_1, \ldots, \mu_m) = \\
             & f(x_1,\ldots, p_q) - \sum_{=1}^{k}\lambda_i h_i (x_1, \ldots, p_q) - \sum_{j=1}^{m}\mu_j g_j (x_1, \ldots, p_q)
        \end{aligned}
\end{equation*}\]

